var PHS = eq.PHS;

//------------імітація -----------
if (eq.smOn.val===true) {
    if (PHS.COOLING.CTRL.STA_RUNNING===true)
    {
        eq.TT1.val = eq.TT1.val - 0.5;  
    }
    if (PHS.LOADING.CTRL.STA_RUNNING===true)
    {
        eq.TT1.val = 25.0;
        eq.LT1.val = eq.LT1.val + 2.5;
    }
    if (PHS.UNLOADING.CTRL.STA_RUNNING===true)
    {
        eq.LT1.val = eq.LT1.val - 2.5;
    }    
    if (PHS.UNLOADING.CTRL.STA_STOPPED===true || PHS.UNLOADING.CTRL.STA_COMPLETE===true)
    {
        eq.TT1.val = 25.0;
    }
    if (eq.TT1.val <2.0) eq.TT1.val=2.0; 
    if (eq.TT1.val >90.0) eq.TT1.val=90.0;
    if (eq.LT1.val <0.0) eq.LT1.val=0.0;
    if (eq.LT1.val >100.0) eq.LT1.val=100.0;
